/**
* Create a single BlobExampleRow from a com.orindasoft.pub.ReadOnlyRowSet
* @param com.orindasoft.pub.ReadOnlyRowSet theRowSet - A ReadOnlyRowSet that is a row from DBHELL.BLOB_EXAMPLE
* @return BlobExampleRow
**/
public BlobExampleRow processOneBlobExampleRow(com.orindasoft.pub.ReadOnlyRowSet theRowSet) throws CSException
  {
  BlobExampleRow new_record = null;

  if (theRowSet.size() > 0)
    {
    new_record = new BlobExampleRow();
    theRowSet.first();
    Object[] theArray = theRowSet.getCurrentRow();

    new_record.setRowName((String)theArray[0]);
   
    if (theArray[1] != null)
      {
      new_record.lobRowBlobdata = (oracle.sql.BLOB)theArray[1];
      }
    }

  return(new_record);
  }

/**
* Create an arbitrary number of BlobExampleRow from a com.orindasoft.pub.ReadOnlyRowSet
* @param com.orindasoft.pub.ReadOnlyRowSet theRowSet
* @return BlobExampleRow[]
**/
public BlobExampleRow[] processManyBlobExampleRows(com.orindasoft.pub.ReadOnlyRowSet theRowSet, int howMany) throws CSException
  {
  // Limit size of array if requested...
  int size = theRowSet.size();
  if (howMany < size)
    {
    size = howMany;
    }

  BlobExampleRow[] new_rows = null;
  BlobExampleRow new_record = null;

  // Only initialize array if ReadOnlyRowSet has data...
  if (size > 0)
    {
    new_rows =  new BlobExampleRow[size];
    }

  // Loop through ReadOnlyRowSet and populate our array
  for (int i=0; i < size; i++)
    {
    theRowSet.setCurrentRowNumber(i);
    new_record = new BlobExampleRow();
    Object[] theArray = theRowSet.getCurrentRow();

    new_record.setRowName((String)theArray[0]);
   
    if (theArray[1] != null)
      {
      new_record.lobRowBlobdata = (oracle.sql.BLOB)theArray[1];
      }

    new_rows[i] = new_record;
    }

  return(new_rows);
  }

/**
* Download LOB's associated with a BlobExampleRow into files.
* @param BlobExampleRow theRow - The row we want to process
* @param String filenameBlobdata Full file name for downloaded LOB. Passing in null means you don't want it downloaded.
* @return BlobExampleRow The row with LOB pointers downloaded into files
* @throws CSException when something goes wrong while downloading.
**/
public BlobExampleRow downloadBlobExampleLobs(BlobExampleRow theRow
                                             ,String filenameBlobdata
                                             ) throws CSException
{
try
  {
  // download BLOB associated with column BLOBDATA
  if (filenameBlobdata != null && theRow.lobRowBlobdata != null)
    {
    java.io.File tempFile = new java.io.File (filenameBlobdata);
    
    theRow.filenameRowBlobdata = tempFile.getAbsolutePath();
    theRow.rowBlobdata = tempFile;
    startStatsTimer();
    theRow.rowBlobdata = objectLoader.unloadBinaryStream(tempFile, theRow.lobRowBlobdata.binaryStreamValue(), "BLOB");
    incRetrieveTime();
    }
  }
catch (Exception e)
  {
  throw new CSException("Error While downloading LOB Column BLOBDATA: " + e.getMessage());
  }
 
return(theRow);
}
   
