public FrequenciesRow[] processManyFrequenciesRows(com.orindasoft.pub.ReadOnlyRowSet theRowSet, int howMany) throws CSException
  {
  // Limit size of array if requested...
  int size = theRowSet.size();
  if (howMany < size)
    {
    size = howMany;
    }

  FrequenciesRow[] new_rows = null;
  FrequenciesRow new_record = null;

  // Only initialize array if ReadOnlyRowSet has data...
  if (size > 0)
    {
    new_rows =  new FrequenciesRow[size];
    }

  // Loop through ReadOnlyRowSet and populate our array
  for (int i=0; i < size; i++)
    {
    theRowSet.setCurrentRowNumber(i);
    new_record = new FrequenciesRow();
    Object[] theArray = theRowSet.getCurrentRow();

    new_record.setRowFrequencyName((String)theArray[0]);
    new_record.setRowCalenderName((String)theArray[1]);
   
    if (theArray[2] != null)
      {
      new_record.setRowFrequencyDescription((String)theArray[2]);
      }
    new_record.setRowNativeYOrN((String)theArray[3]);
   
    if (theArray[4] != null)
      {
      new_record.setRowFormattedDateValue((String)theArray[4]);
      }
   
    if (theArray[5] != null)
      {
      new_record.setRowOracleFormatMask((String)theArray[5]);
      }
    new_record.setRowBusinessDayOnlyYOrN((String)theArray[6]);
   
    if (theArray[7] != null)
      {
      new_record.setRowDateOffset((java.math.BigDecimal)theArray[7]);
      }
   
    if (theArray[8] != null)
      {
      new_record.setRowFrequencyType((String)theArray[8]);
      }

    new_rows[i] = new_record;
    }

  return(new_rows);
  }
