  /** 
  * Release all Oracle db resources that we know to be in use. 
  *  @return boolean <code>true</code> if we didn't encounter any problems, otherwise <code>false</code> 
  */ 
   
  public boolean releaseResources() 
    { 
    boolean returnCode = true; 
    
    theLog.debug("PubParametersMgr - Attempting to release connection");
    try 
      { 
      
      if (thePubParametersIns != null)
        { 
        thePubParametersIns.releaseResources(); 
        } 
      
      if (PubParametersUpd != null)
        { 
        PubParametersUpd.releaseResources(); 
        } 
      
      if (thePubParametersDel != null)
        { 
        thePubParametersDel.releaseResources(); 
        } 
      
      if (theSynParameterValuesR2 != null)
        { 
        theSynParameterValuesR2.releaseResources(); 
        } 
      
      if (thePubParametersParsPkLck != null)
        { 
        thePubParametersParsPkLck.releaseResources(); 
        } 
      
      if (thePubParametersParsPk != null)
        { 
        thePubParametersParsPk.releaseResources(); 
        } 
      } 
    catch (Exception e) 
      { 
      incErrorCount();
      theLog.warning(e.toString()); 
      returnCode = false; 
      } 
    
    theConnection = null; 
    incReleaseCount(); 
    theLog.debug("PubParametersMgr- Finished releasing connection");
    
    return(returnCode); 
    } 
  
