public PubServerLockRow processOnePubServerLockRow(com.orindasoft.pub.ReadOnlyRowSet theRowSet) throws CSException
  {
  PubServerLockRow new_record = null;

  if (theRowSet.size() > 0)
    {
    new_record = new PubServerLockRow();
    theRowSet.first();
    Object[] theArray = theRowSet.getCurrentRow();

    new_record.setRowServerName((String)theArray[0]);
   
    if (theArray[1] != null)
      {
      new_record.setRowCycleCount((java.math.BigDecimal)theArray[1]);
      }
    }

  return(new_record);
  }

/**
* Create an arbitrary number of PubServerLockRow from a com.orindasoft.pub.ReadOnlyRowSet
* @param com.orindasoft.pub.ReadOnlyRowSet theRowSet
* @return PubServerLockRow[]
**/
public PubServerLockRow[] processManyPubServerLockRows(com.orindasoft.pub.ReadOnlyRowSet theRowSet, int howMany) throws CSException
  {
  // Limit size of array if requested...
  int size = theRowSet.size();
  if (howMany < size)
    {
    size = howMany;
    }

  PubServerLockRow[] new_rows = null;
  PubServerLockRow new_record = null;

  // Only initialize array if ReadOnlyRowSet has data...
  if (size > 0)
    {
    new_rows =  new PubServerLockRow[size];
    }

  // Loop through ReadOnlyRowSet and populate our array
  for (int i=0; i < size; i++)
    {
    theRowSet.setCurrentRowNumber(i);
    new_record = new PubServerLockRow();
    Object[] theArray = theRowSet.getCurrentRow();

    new_record.setRowServerName((String)theArray[0]);
   
    if (theArray[1] != null)
      {
      new_record.setRowCycleCount((java.math.BigDecimal)theArray[1]);
      }

    new_rows[i] = new_record;
    }

  return(new_rows);
  }
   
