public ServerLockRow processOneServerLockRow(com.orindasoft.pub.ReadOnlyRowSet theRowSet) throws CSException
  {
  ServerLockRow new_record = null;

  if (theRowSet.size() > 0)
    {
    new_record = new ServerLockRow();
    theRowSet.first();
    Object[] theArray = theRowSet.getCurrentRow();

    new_record.setRowServerName((String)theArray[0]);
   
    if (theArray[1] != null)
      {
      new_record.setRowCycleCount((java.math.BigDecimal)theArray[1]);
      }
    }

  return(new_record);
  }

/**
* Create an arbitrary number of ServerLockRow from a com.orindasoft.pub.ReadOnlyRowSet
* @param com.orindasoft.pub.ReadOnlyRowSet theRowSet
* @return ServerLockRow[]
**/
public ServerLockRow[] processManyServerLockRows(com.orindasoft.pub.ReadOnlyRowSet theRowSet, int howMany) throws CSException
  {
  // Limit size of array if requested...
  int size = theRowSet.size();
  if (howMany < size)
    {
    size = howMany;
    }

  ServerLockRow[] new_rows = null;
  ServerLockRow new_record = null;

  // Only initialize array if ReadOnlyRowSet has data...
  if (size > 0)
    {
    new_rows =  new ServerLockRow[size];
    }

  // Loop through ReadOnlyRowSet and populate our array
  for (int i=0; i < size; i++)
    {
    theRowSet.setCurrentRowNumber(i);
    new_record = new ServerLockRow();
    Object[] theArray = theRowSet.getCurrentRow();

    new_record.setRowServerName((String)theArray[0]);
   
    if (theArray[1] != null)
      {
      new_record.setRowCycleCount((java.math.BigDecimal)theArray[1]);
      }

    new_rows[i] = new_record;
    }

  return(new_rows);
  }
   
