public TestRow processOneTestRow(com.orindasoft.pub.ReadOnlyRowSet theRowSet) throws CSException
  {
  TestRow new_record = null;

  if (theRowSet.size() > 0)
    {
    new_record = new TestRow();
    theRowSet.first();
    Object[] theArray = theRowSet.getCurrentRow();

   
    if (theArray[0] != null)
      {
      new_record.setRowRawCol((byte[])theArray[0]);
      }
    }

  return(new_record);
  }

public TestRow[] processManyTestRows(com.orindasoft.pub.ReadOnlyRowSet theRowSet, int howMany) throws CSException
  {
  int size = theRowSet.size();
  if (howMany < size)
    {
    size = howMany;
    }

  TestRow[] new_rows = null;
  TestRow new_record = null;

  if (size > 0)
    {
    new_rows =  new TestRow[size];
    }

  for (int i=0; i < size; i++)
    {
    theRowSet.setCurrentRowNumber(i);
    new_record = new TestRow();
    Object[] theArray = theRowSet.getCurrentRow();

   
    if (theArray[0] != null)
      {
      new_record.setRowRawCol((byte[])theArray[0]);
      }

    new_rows[i] = new_record;
    }

  return(new_rows);
  }
   
