  /** 
  * Release all Oracle db resources that we know to be in use. 
  *  @return boolean <code>true</code> if we didn't encounter any problems, otherwise <code>false</code> 
  */ 
   
  public boolean releaseResources() 
    { 
    boolean returnCode = true; 
    
    theLog.debug("ParameterTypesMgr - Attempting to release connection");
    try 
      { 
      
      if (theParameterTypesIns != null)
        { 
        theParameterTypesIns.releaseResources(); 
        } 
      
      if (ParameterTypesUpd != null)
        { 
        ParameterTypesUpd.releaseResources(); 
        } 
      
      if (theParameterTypesDel != null)
        { 
        theParameterTypesDel.releaseResources(); 
        } 
      
      if (theParameterValuesR1 != null)
        { 
        theParameterValuesR1.releaseResources(); 
        } 
      
      if (theParameterTypesPtypPkLck != null)
        { 
        theParameterTypesPtypPkLck.releaseResources(); 
        } 
      
      if (theParameterTypesPtypPk != null)
        { 
        theParameterTypesPtypPk.releaseResources(); 
        } 
      } 
    catch (Exception e) 
      { 
      incErrorCount();
      theLog.warning(e.toString()); 
      returnCode = false; 
      } 
    
    theConnection = null; 
    incReleaseCount(); 
    theLog.debug("ParameterTypesMgr- Finished releasing connection");
    
    return(returnCode); 
    } 
  
