/**
* Create a single ParameterValuesRow from a com.orindasoft.pub.ReadOnlyRowSet
* @param com.orindasoft.pub.ReadOnlyRowSet theRowSet - A ReadOnlyRowSet that is a row from SYNUSER.PARAMETER_VALUES
* @return ParameterValuesRow
**/
public ParameterValuesRow processOneParameterValuesRow(com.orindasoft.pub.ReadOnlyRowSet theRowSet) throws CSException
  {
  ParameterValuesRow new_record = null;

  if (theRowSet.size() > 0)
    {
    new_record = new ParameterValuesRow();
    theRowSet.first();
    Object[] theArray = theRowSet.getCurrentRow();

    new_record.setRowParameterName((String)theArray[0]);
    new_record.setRowParamtypeName((String)theArray[1]);
   
    if (theArray[2] != null)
      {
      new_record.setRowParamValue((String)theArray[2]);
      }
    }

  return(new_record);
  }

/**
* Create an arbitrary number of ParameterValuesRow from a com.orindasoft.pub.ReadOnlyRowSet
* @param com.orindasoft.pub.ReadOnlyRowSet theRowSet
* @return ParameterValuesRow[]
**/
public ParameterValuesRow[] processManyParameterValuesRows(com.orindasoft.pub.ReadOnlyRowSet theRowSet, int howMany) throws CSException
  {
  // Limit size of array if requested...
  int size = theRowSet.size();
  if (howMany < size)
    {
    size = howMany;
    }

  ParameterValuesRow[] new_rows = null;
  ParameterValuesRow new_record = null;

  // Only initialize array if ReadOnlyRowSet has data...
  if (size > 0)
    {
    new_rows =  new ParameterValuesRow[size];
    }

  // Loop through ReadOnlyRowSet and populate our array
  for (int i=0; i < size; i++)
    {
    theRowSet.setCurrentRowNumber(i);
    new_record = new ParameterValuesRow();
    Object[] theArray = theRowSet.getCurrentRow();

    new_record.setRowParameterName((String)theArray[0]);
    new_record.setRowParamtypeName((String)theArray[1]);
   
    if (theArray[2] != null)
      {
      new_record.setRowParamValue((String)theArray[2]);
      }

    new_rows[i] = new_record;
    }

  return(new_rows);
  }
   
