  /**                                             
  * Validate a BlobTestRow
  * @param BlobTestRow theRow The row to be validated
  * @throws CSColumnException When the row has a missing NOT NULL column
  * @throws CSColumnException When the row has a field that won't fit into its database column
  * @since 4.0.1709
  * 
  */                                              
  public void rowValidate( BlobTestRow theRow) throws CSColumnException 
    { 
    theLog.debug("Validating Row");
    // Column rowSeqno can not be more than 38 digits
    if (theRow.rowSeqno != null && theRow.rowSeqno.abs().doubleValue() > (java.lang.Math.pow(10,38) - 1) )
      {
      throw new CSColumnException("BLOB_TEST","SEQNO",CSColumnException.COLUMN_LENGTH_EXCEEDED,theRow.rowSeqno,true,38,-2147483648,"BLOB_TEST.SEQNO is too big");
      }
      
    } 
    
/**
* Create a single BlobTestRow from a com.orindasoft.pub.ReadOnlyRowSet
* @param com.orindasoft.pub.ReadOnlyRowSet theRowSet - A ReadOnlyRowSet that is a row from DBHELL.BLOB_TEST
* @return BlobTestRow
**/
public BlobTestRow processOneBlobTestRow(com.orindasoft.pub.ReadOnlyRowSet theRowSet) throws CSException
  {
  BlobTestRow new_record = null;

  if (theRowSet.size() > 0)
    {
    new_record = new BlobTestRow();
    theRowSet.first();
    Object[] theArray = theRowSet.getCurrentRow();

   
    if (theArray[0] != null)
      {
      new_record.setRowSeqno((java.math.BigDecimal)theArray[0]);
      }
   
    if (theArray[1] != null)
      {
      new_record.lobRowBlobColumn = (oracle.sql.BLOB)theArray[1];
      }
    }

  return(new_record);
  }

/**
* Create an arbitrary number of BlobTestRow from a com.orindasoft.pub.ReadOnlyRowSet
* @param com.orindasoft.pub.ReadOnlyRowSet theRowSet
* @return BlobTestRow[]
**/
public BlobTestRow[] processManyBlobTestRows(com.orindasoft.pub.ReadOnlyRowSet theRowSet, int howMany) throws CSException
  {
  // Limit size of array if requested...
  int size = theRowSet.size();
  if (howMany < size)
    {
    size = howMany;
    }

  BlobTestRow[] new_rows = null;
  BlobTestRow new_record = null;

  // Only initialize array if ReadOnlyRowSet has data...
  if (size > 0)
    {
    new_rows =  new BlobTestRow[size];
    }

  // Loop through ReadOnlyRowSet and populate our array
  for (int i=0; i < size; i++)
    {
    theRowSet.setCurrentRowNumber(i);
    new_record = new BlobTestRow();
    Object[] theArray = theRowSet.getCurrentRow();

   
    if (theArray[0] != null)
      {
      new_record.setRowSeqno((java.math.BigDecimal)theArray[0]);
      }
   
    if (theArray[1] != null)
      {
      new_record.lobRowBlobColumn = (oracle.sql.BLOB)theArray[1];
      }

    new_rows[i] = new_record;
    }

  return(new_rows);
  }

/**
* Download LOB's associated with a BlobTestRow into files.
* @param BlobTestRow theRow - The row we want to process
* @param String filenameBlobColumn Full file name for downloaded LOB. Passing in null means you don't want it downloaded.
* @return BlobTestRow The row with LOB pointers downloaded into files
* @throws CSException when something goes wrong while downloading.
**/
public BlobTestRow downloadBlobTestLobs(BlobTestRow theRow
                                       ,String filenameBlobColumn
                                       ) throws CSException
{
try
  {
  // download BLOB associated with column BLOB_COLUMN
  if (filenameBlobColumn != null && theRow.lobRowBlobColumn != null)
    {
    java.io.File tempFile = new java.io.File (filenameBlobColumn);
    
    theRow.filenameRowBlobColumn = tempFile.getAbsolutePath();
    theRow.rowBlobColumn = tempFile;
    startStatsTimer();
    theRow.rowBlobColumn = objectLoader.unloadBinaryStream(tempFile, theRow.lobRowBlobColumn.binaryStreamValue(), "BLOB");
    incRetrieveTime();
    }
  }
catch (Exception e)
  {
  throw new CSException("Error While downloading LOB Column BLOB_COLUMN: " + e.getMessage());
  }
 
return(theRow);
}
   
