/**
* Create a single ClobExampleRow from a com.orindasoft.pub.ReadOnlyRowSet
* @param com.orindasoft.pub.ReadOnlyRowSet theRowSet - A ReadOnlyRowSet that is a row from DBHELL.CLOB_EXAMPLE
* @return ClobExampleRow
**/
public ClobExampleRow processOneClobExampleRow(com.orindasoft.pub.ReadOnlyRowSet theRowSet) throws CSException
  {
  ClobExampleRow new_record = null;

  if (theRowSet.size() > 0)
    {
    new_record = new ClobExampleRow();
    theRowSet.first();
    Object[] theArray = theRowSet.getCurrentRow();

    new_record.setRowName((String)theArray[0]);
   
    if (theArray[1] != null)
      {
      new_record.lobRowClobdata = (oracle.sql.CLOB)theArray[1];
      }
    }

  return(new_record);
  }

/**
* Create an arbitrary number of ClobExampleRow from a com.orindasoft.pub.ReadOnlyRowSet
* @param com.orindasoft.pub.ReadOnlyRowSet theRowSet
* @return ClobExampleRow[]
**/
public ClobExampleRow[] processManyClobExampleRows(com.orindasoft.pub.ReadOnlyRowSet theRowSet, int howMany) throws CSException
  {
  // Limit size of array if requested...
  int size = theRowSet.size();
  if (howMany < size)
    {
    size = howMany;
    }

  ClobExampleRow[] new_rows = null;
  ClobExampleRow new_record = null;

  // Only initialize array if ReadOnlyRowSet has data...
  if (size > 0)
    {
    new_rows =  new ClobExampleRow[size];
    }

  // Loop through ReadOnlyRowSet and populate our array
  for (int i=0; i < size; i++)
    {
    theRowSet.setCurrentRowNumber(i);
    new_record = new ClobExampleRow();
    Object[] theArray = theRowSet.getCurrentRow();

    new_record.setRowName((String)theArray[0]);
   
    if (theArray[1] != null)
      {
      new_record.lobRowClobdata = (oracle.sql.CLOB)theArray[1];
      }

    new_rows[i] = new_record;
    }

  return(new_rows);
  }

/**
* Download LOB's associated with a ClobExampleRow into files.
* @param ClobExampleRow theRow - The row we want to process
* @param String filenameClobdata Full file name for downloaded LOB. Passing in null means you don't want it downloaded.
* @return ClobExampleRow The row with LOB pointers downloaded into files
* @throws CSException when something goes wrong while downloading.
**/
public ClobExampleRow downloadClobExampleLobs(ClobExampleRow theRow
                                             ,String filenameClobdata
                                             ) throws CSException
{
try
  {
  // download CLOB associated with column CLOBDATA
  if (filenameClobdata != null && theRow.lobRowClobdata != null)
    {
    java.io.File tempFile = new java.io.File (filenameClobdata);
    
    theRow.filenameRowClobdata = tempFile.getAbsolutePath();
    theRow.rowClobdata = tempFile;
    startStatsTimer();
    theRow.rowClobdata = objectLoader.unloadBinaryStream(tempFile, theRow.lobRowClobdata.binaryStreamValue(), "CLOB");
    incRetrieveTime();
    }
  }
catch (Exception e)
  {
  throw new CSException("Error While downloading LOB Column CLOBDATA: " + e.getMessage());
  }
 
return(theRow);
}
   
