/**
* Create an arbitrary number of DeptRow from a com.orindasoft.pub.ReadOnlyRowSet
* @param com.orindasoft.pub.ReadOnlyRowSet theRowSet
* @return DeptRow[]
**/
public DeptRow[] processManyDeptRows(com.orindasoft.pub.ReadOnlyRowSet theRowSet, int howMany) throws CSException
  {
  // Limit size of array if requested...
  int size = theRowSet.size();
  if (howMany < size)
    {
    size = howMany;
    }

  DeptRow[] new_rows = null;
  DeptRow new_record = null;

  // Only initialize array if ReadOnlyRowSet has data...
  if (size > 0)
    {
    new_rows =  new DeptRow[size];
    }

  // Loop through ReadOnlyRowSet and populate our array
  for (int i=0; i < size; i++)
    {
    theRowSet.setCurrentRowNumber(i);
    new_record = new DeptRow();
    Object[] theArray = theRowSet.getCurrentRow();

   
    if (theArray[0] != null)
      {
      new_record.setRowDeptno((java.math.BigDecimal)theArray[0]);
      }
   
    if (theArray[1] != null)
      {
      new_record.setRowDname((String)theArray[1]);
      }
   
    if (theArray[2] != null)
      {
      new_record.setRowLoc((String)theArray[2]);
      }

    new_rows[i] = new_record;
    }

  return(new_rows);
  }
   
