  public boolean releaseResources() 
    { 
    boolean returnCode = true; 
    
    theLog.debug("SynParametersMgr - Attempting to release connection");
    try 
      { 
      
      if (theSynParametersIns != null)
        { 
        theSynParametersIns.releaseResources(); 
        } 
      
      if (SynParametersUpd != null)
        { 
        SynParametersUpd.releaseResources(); 
        } 
      
      if (theSynParametersDel != null)
        { 
        theSynParametersDel.releaseResources(); 
        } 
      
      if (theSynParameterValuesR2 != null)
        { 
        theSynParameterValuesR2.releaseResources(); 
        } 
      
      if (theSynParametersParsPkLck != null)
        { 
        theSynParametersParsPkLck.releaseResources(); 
        } 
      
      if (theSynParametersParsPk != null)
        { 
        theSynParametersParsPk.releaseResources(); 
        } 
      } 
    catch (Exception e) 
      { 
      incErrorCount();
      theLog.warning(e.toString()); 
      returnCode = false; 
      } 
    
    theConnection = null; 
    incReleaseCount(); 
    theLog.debug("SynParametersMgr- Finished releasing connection");
    
    return(returnCode); 
    } 
  
